<template>
  <div :class="className" v-if="loadFlag">
    <img
      src="data:image/gif;base64,"
      :class="classNameImg" v-if="delayloading">
  </div>
</template>

<script>

export default {
  props: {
    loadFlag: {
      type: Boolean,
      default: () => {
        return false
      }
    },
    className: {
      type: String,
      default: () => {
        return 'fullscreen-loading'
      }
    },
    classNameImg: {
      type: String,
      default: () => {
        return 'normal-size-loading'
      }
    },
    delayTime: {
      type: Number,
      default: () => {
        return 1000
      }
    }
  },
  data() {
    return {
      delayloading: false,
      delayFun: null
    }
  },
  watch: {
    loadFlag: {
      handler(val) {
        if (val) {
          clearTimeout(this.delayFun)
          this.delayFun = setTimeout(() => {
            this.delayloading = true
          }, this.delayTime)
        } else {
          clearTimeout(this.delayFun)
          this.delayloading = false
        }
      },
      immediate: true
    }
  }

}
</script>

<style lang="less" scoped>
.fullscreen-loading {
  background: rgba(255, 255, 255, 1);
}
</style>
